sources.src/sass/settings/_variables.scss:
:root {
    --spacer-100: 1rem;

    --clr-neutral-900: hsl(0, 0%, 9%);
    --clr-neutral-700: hsl(0, 0%, 20%);
    --clr-neutral-300: hsl(0, 0%, 81%);
    --clr-neutral-100: hsl(0, 0%, 100%);

    --clr-accent-500: hsl(162, 100%, 30%);
    --clr-accent-400: hsl(148, 76%, 63%);

    --font-fam-primary: "PT Sans", sans-serif;

    --font-size-700: 2.25rem;
    --font-size-600: 1.5rem;
    --font-size-500: 1.25rem;
    --font-size-400: 1.125rem;
    --font-size-300: 0.9375rem;

    --font-weight-700: 700;
    --font-weight-400: 400;

    --box-shadow-100: 0 0.25em 1.5em rgba(0, 0, 0, 0.15);
}

sources.src/sass/generic/_general-styles.scss:
@use "../tools" as *;

*,
*::before,
*::after {
    box-sizing: border-box;
}

h1,
h2,
h3,
h4,
p {
    margin: 0;
}

ul[class],
ol[class] {
    list-style-type: none;
    padding: 0;
}

img {
    max-width: 100%;
    height: auto;
    display: inline-block;
}

a {
    display: inline-block;
    text-decoration: none;
    color: inherit;
}

/* ----- GENERAL STYLES ----- */
html {
    scroll-behavior: smooth;
}

/**
 * Disable smooth scrolling when users have prefers-reduced-motion enabled
 */
@media screen and (prefers-reduced-motion: reduce) {
    html {
        scroll-behavior: auto;
    }
}

body {
    font-family: var(--font-fam-primary);
    font-size: var(--font-size-500);
    font-weight: var(--font-weight-400);
    color: var(--clr-neutral-900);
    margin: 0;
}

a {
    text-decoration: none;
    font-weight: var(--font-weight-700);
}

section {
    padding: 4.125rem 0;

    @include media-query(40em) {
        padding: 8.125rem 0;
    }
}

sources.src/sass/tools/_mixins.scss:
@mixin main-title {
    text-transform: uppercase;
    background-color: var(--fg, var(--clr-neutral-900));
    color: var(--accent, var(--clr-accent-400));
    padding: 0.5rem;
    letter-spacing: 1px;
    line-height: 1.7rem;
    text-align: center;
}

@mixin background-img {
    background-repeat: no-repeat;
    background-size: cover;
}

@mixin media-query($size) {
    @media (min-width: $size) {
        @content;
    }
}

