sources.src/sass/components/_header.scss:
@use "../tools" as *;

.hero {
    margin-top: 3rem;
    --gap: 4.5rem;

    &__text {
        align-self: center;
        display: grid;

        @include media-query(37.5em) {
            padding-bottom: 2.4rem;
        }
    }

    p > a {
        color: var(--clr-accent-500);
    }

    img {
        transform: translateY(2em);
    }

    @include media-query(50em) {
        padding: 0;
    }
}

/* ----- NAVIGATION ----- */
.nav-list {
    display: flex;
    justify-content: center;
    gap: 2em;

    a:link {
        font-size: 1rem;
        text-transform: uppercase;
        letter-spacing: 3px;
    }

    a:hover,
    a:focus {
        color: var(--clr-accent-400);
    }
}

header {
    padding: 0 0 0.5rem;

    @include media-query(40em) {
        position: sticky;
        z-index: 1000;
        top: 0;
    }
}

sources.src/sass/components/_testimonial.scss:
.testimonials {
    max-width: 76.7rem;
    --gap: 4rem;
}

.testimonial {
    padding: calc(var(--spacer-100) * 2);
    max-width: 20.125rem;

    & > p {
        font-size: var(--font-size-500);
        font-style: italic;
        font-weight: 300;
        line-height: 2.1875;
    }

    :nth-child(2) {
        align-self: end;
    }
}

.testimonial .name {
    margin-top: 4rem;
    font-weight: var(--font-weight-700);
    color: var(--accent);
}

.cards {
    padding-top: 6rem;
    align-items: center;
}

