    color: var(--btn-fg, var(--clr-neutral-900));
    background: transparent;
}

.hero__button {
    place-self: center;
}

sources.src/sass/utilities/_helper-class.scss:
@use "../tools" as *;

.shadow {
    box-shadow: var(--box-shadow-100);
}

.container {
    padding: 0 var(--spacer-100);
    max-width: 70rem;
    margin: 0 auto;
}

.flow-content > * + * {
    margin-top: var(--flow-space, var(--spacer-100));
}

.flow-content--large {
    --flow-space: 3rem;
}

.flex {
    display: flex;
    flex-direction: column;

    & > * + * {
        margin: calc(var(--spacer-100) * 3) 0 0 0;
    }

    @include media-query(40em) {
        &:not(.testimonial) {
            flex-direction: row;
            justify-content: space-between;
        }

        .hero__text {
            display: block;
        }

        & > * {
            flex-basis: 100%;
        }

        & > * + * {
            margin: 0 0 0 var(--gap, var(--spacer-100));
        }
    }
}

.flex .a-center {
    align-items: center;
}

.txt-center {
    text-align: center;
}

.m-auto {
    margin: auto;
}

[class*="bg-"] {
    color: var(--fg);
    background-color: var(--bg);
}

.bg--dark {
    --fg: var(--clr-neutral-100);
    --bg: var(--clr-neutral-900);
    --accent: var(--clr-accent-400);
    --btn-border: var(--clr-accent-500);
    --btn-fg: var(--clr-accent-500);
}

.bg--accent {
    --fg: var(--clr-neutral-900);
    --bg: var(--clr-accent-400);
    --accent: var(--clr-neutral-100);
    --btn-border: var(--clr-neutral-100);
    --btn-fg: var(--clr-neutral-100);
}

.border {
    position: relative;

    &:nth-of-type(2n) {
        --accent: var(--clr-neutral-900);
    }

    ::before {
        content: "";
        display: block;
        width: 5rem;
        height: 5rem;
        background: var(--accent, var(--clr-accent-400));
        position: absolute;
        z-index: -2;
        top: -0.6rem;
        left: -0.6rem;
    }
}
