:root {
  --spacer-100: 1rem;
  --clr-neutral-900: hsl(0, 0%, 9%);
  --clr-neutral-700: hsl(0, 0%, 20%);
  --clr-neutral-300: hsl(0, 0%, 81%);
  --clr-neutral-100: hsl(0, 0%, 100%);
  --clr-accent-500: hsl(162, 100%, 30%);
  --clr-accent-400: hsl(148, 76%, 63%);
  --font-fam-primary: "PT Sans", sans-serif;
  --font-size-700: 2.25rem;
  --font-size-600: 1.5rem;
  --font-size-500: 1.25rem;
  --font-size-400: 1.125rem;
  --font-size-300: 0.9375rem;
  --font-weight-700: 700;
  --font-weight-400: 400;
  --box-shadow-100: 0 0.25em 1.5em rgba(0, 0, 0, 0.15);
}

*,
*::before,
*::after {
  box-sizing: border-box;
}

h1,
h2,
h3,
h4,
p {
  margin: 0;
}

ul[class],
ol[class] {
  list-style-type: none;
  padding: 0;
}

img {
  max-width: 100%;
  height: auto;
  display: inline-block;
}

a {
  display: inline-block;
  text-decoration: none;
  color: inherit;
}

/* ----- GENERAL STYLES ----- */
html {
  scroll-behavior: smooth;
}

/**
 * Disable smooth scrolling when users have prefers-reduced-motion enabled
 */
@media screen and (prefers-reduced-motion: reduce) {
  html {
    scroll-behavior: auto;
  }
}
body {
  font-family: var(--font-fam-primary);
  font-size: var(--font-size-500);
  font-weight: var(--font-weight-400);
  color: var(--clr-neutral-900);
  margin: 0;
}

a {
  text-decoration: none;
  font-weight: var(--font-weight-700);
}

section {
  padding: 4.125rem 0;
}
@media (min-width: 40em) {
  section {
    padding: 8.125rem 0;
  }
}

.logo {
  width: 3rem;
  height: 3rem;
  position: absolute;
  top: 1.8rem;
  left: 2rem;
  opacity: 0;
}
@media (min-width: 40em) {
  .logo {
    opacity: 1;
  }
}

h1 {
  font-weight: var(--font-weight-400);
}

.section__title {
  text-transform: uppercase;
  background-color: var(--fg, var(--clr-neutral-900));
  color: var(--accent, var(--clr-accent-400));
  padding: 0.5rem;
  letter-spacing: 1px;
  line-height: 1.7rem;
  text-align: center;
  display: inline-block;
  font-weight: var(--font-weight-400);
}
.section__subtitle {
  font-size: var(--font-size-700);
  line-height: 2.8rem;
}
.section__subtitle b {
  font-weight: var(--font-weight-700);
}

.hero {
  margin-top: 3rem;
  --gap: 4.5rem;
}
.hero__text {
  align-self: center;
  display: grid;
}
@media (min-width: 37.5em) {
  .hero__text {
    padding-bottom: 2.4rem;
  }
}
.hero p > a {
  color: var(--clr-accent-500);
}
.hero img {
  transform: translateY(2em);
}
@media (min-width: 50em) {
  .hero {
    padding: 0;
  }
}

/* ----- NAVIGATION ----- */
.nav-list {
  display: flex;
  justify-content: center;
  gap: 2em;
}
.nav-list a:link {
  font-size: 1rem;
  text-transform: uppercase;
  letter-spacing: 3px;
}
.nav-list a:hover,
.nav-list a:focus {
  color: var(--clr-accent-400);
}

header {
  padding: 0 0 0.5rem;
}
@media (min-width: 40em) {
  header {
    position: sticky;
    z-index: 1000;
    top: 0;
  }
}

.testimonials {
  max-width: 76.7rem;
  --gap: 4rem;
}

.testimonial {
  padding: calc(var(--spacer-100) * 2);
  max-width: 20.125rem;
}
.testimonial > p {
  font-size: var(--font-size-500);
  font-style: italic;
  font-weight: 300;
  line-height: 2.1875;
}
.testimonial :nth-child(2) {
  align-self: end;
}

.testimonial .name {
  margin-top: 4rem;
  font-weight: var(--font-weight-700);
  color: var(--accent);
}

.cards {
  padding-top: 6rem;
  align-items: center;
}

/* ----- MASTERING SECTION ----- */
.mastering {
  background-image: url(/8f541f0d91edec3390d06280968d2027.jpg);
}
.mastering h2 {
  color: var(--accent);
}

/* ----- START LEARNING ----- */
.start-learning {
  background-repeat: no-repeat;
  background-size: cover;
  background-blend-mode: multiply;
  text-align: center;
}

.start-learning.start-learning1 {
  background-image: url(/1eec6211df13b430e31191aa06b63c2e.jpg);
}

.start-learning.start-learning2 {
  background-image: url(/f75095dca5a6d2b717bdfac39e67dfda.jpg);
}

.button {
  text-transform: uppercase;
  letter-spacing: 0.1rem;
  font-weight: 700;
  font-size: 1.125rem;
  padding: 0.75em 1.5em;
  background-color: var(--accent, var(--clr-accent-400));
  color: var(--fg, var(--clr-neutral-900));
  border: 3px solid transparent;
  cursor: pointer;
}

.button:hover,
.button:focus {
  border: 3px solid var(--btn-border, var(--clr-neutral-900));
  color: var(--btn-fg, var(--clr-neutral-900));
  background: transparent;
}

.hero__button {
  place-self: center;
}

.shadow {
  box-shadow: var(--box-shadow-100);
}

.container {
  padding: 0 var(--spacer-100);
  max-width: 70rem;
  margin: 0 auto;
}

.flow-content > * + * {
  margin-top: var(--flow-space, var(--spacer-100));
}

.flow-content--large {
  --flow-space: 3rem;
}

.flex {
  display: flex;
  flex-direction: column;
}
.flex > * + * {
  margin: calc(var(--spacer-100) * 3) 0 0 0;
}
@media (min-width: 40em) {
  .flex:not(.testimonial) {
    flex-direction: row;
    justify-content: space-between;
  }
  .flex .hero__text {
    display: block;
  }
  .flex > * {
    flex-basis: 100%;
  }
  .flex > * + * {
    margin: 0 0 0 var(--gap, var(--spacer-100));
  }
}

.flex .a-center {
  align-items: center;
}

.txt-center {
  text-align: center;
}

.m-auto {
  margin: auto;
}

[class*=bg-] {
  color: var(--fg);
  background-color: var(--bg);
}

.bg--dark {
  --fg: var(--clr-neutral-100);
  --bg: var(--clr-neutral-900);
  --accent: var(--clr-accent-400);
  --btn-border: var(--clr-accent-500);
  --btn-fg: var(--clr-accent-500);
}

.bg--accent {
  --fg: var(--clr-neutral-900);
  --bg: var(--clr-accent-400);
  --accent: var(--clr-neutral-100);
  --btn-border: var(--clr-neutral-100);
  --btn-fg: var(--clr-neutral-100);
}

.border {
  position: relative;
}
.border:nth-of-type(2n) {
  --accent: var(--clr-neutral-900);
}
.border ::before {
  content: "";
  display: block;
  width: 5rem;
  height: 5rem;
  background: var(--accent, var(--clr-accent-400));
  position: absolute;
  z-index: -2;
  top: -0.6rem;
  left: -0.6rem;
}

